import { AnyObject } from './any-object';
export interface GraphQLResponseRoot {
    data?: AnyObject | null;
    errors?: Array<GraphQLResponseError>;
}
export interface GraphQLResponseError {
    message: string;
    locations?: Array<GraphQLResponseErrorLocation>;
    extensions?: GraphQLResonseErrorExtension;
    [propName: string]: unknown;
}
export interface GraphQLResonseErrorExtension {
    code: string;
    nacelleErrorId?: string;
}
export interface GraphQLResponseErrorLocation {
    line: number;
    column: number;
}
export interface GraphQLPageResponse<T> {
    items: Array<T>;
    nextToken: string;
}
export interface Edge<T> {
    cursor: string;
    node: T;
}
export interface Connection<T> {
    pageInfo: {
        hasNextPage: boolean;
    };
    edges: Array<Edge<T>>;
}
