import contentFragment from '../fragments/content';

const contentQuery = `
query content(
  $filter: ContentFilterInput
) {
  content(filter: $filter) {
    ${contentFragment}
  }
}
`;

const allContentQuery = `
query allContent($filter: ContentFilterInput) {
  allContent(filter: $filter) {
    pageInfo {
      hasNextPage
    }
    edges {
      cursor
      node {
        ${contentFragment}
      }
    }
  }
}
`;

export { contentQuery, allContentQuery };
