import { productFragmentSubset } from '../fragments/product';
import productContentFragment from '../fragments/product-content';
import { variantFragment } from '../fragments/variant';

const productsQuery = `
query products(
  $filter: ProductFilterInput,
) {
  products(filter: $filter) {
    ${productFragmentSubset}
    variants {
      ${variantFragment}
    }
    content {
      ${productContentFragment}
    }
  }
}
`;

const allProductsQuery = `
query allProducts($filter: ProductFilterInput) {
  allProducts(filter: $filter) {
    pageInfo {
      hasNextPage
    }
    edges {
      cursor
      node {
        ${productFragmentSubset}
        variants {
          ${variantFragment}
        }
        content {
          ${productContentFragment}
        }
      }
    }
  }
}
`;

export { productsQuery, allProductsQuery };
