import {
  Content,
  Product,
  ProductContent,
  Variant,
  VariantContent,
  CollectionContent,
  ContentCollection,
  ProductCollection,
} from './graphql-generated';

export enum EntryType {
  PRODUCT = 'product',
  PRODUCT_CONTENT = 'product-content',
  VARIANT = 'variant',
  VARIANT_CONTENT = 'variant-content',
  CONTENT = 'content',
  CONTENT_COLLECTION = 'content-collection',
  PRODUCT_COLLECTION = 'product-collection',
  COLLECTION_CONTENT = 'collection-content',
}

export type Entry =
  | Content
  | Product
  | ProductContent
  | Variant
  | VariantContent
  | ContentCollection
  | ProductCollection
  | CollectionContent;

export enum DataSourceName {
  SHOPIFY = 'shopify',
  CONTENTFUL = 'contentful',
  SANITY = 'sanity',
}
