export interface HttpClient {
  options?: RequestInit;
  get<T>(url: string): Promise<HttpClientResponse<T> | null | void>;
  post<T>(
    url: string,
    data: unknown
  ): Promise<HttpClientResponse<T> | null | void>;
}

export interface HttpClientResponse<T> {
  data: T;
  headers: Headers;
  status: number;
  statusText?: number | string;
}
