import { HttpClient } from '../types/http';

export interface StorefrontConnectorParams {
  token: string;
  endpoint: string;
  locale: string;
  httpClient?: HttpClient;
  debugMode?: boolean;
}

export interface RequestParams {
  query: string;
  queryName?: string;
  variables?: unknown;
}

export interface StorefrontRequestResponse<T> {
  success: boolean;
  data: T | null;
  error?: string;
}
